import { createSlice } from "@reduxjs/toolkit"

const cartUiSlice = createSlice({
  name: "cartUi",
  initialState: false,
  reducers: {
    toggle(state) {
      state.cartIsVisible = !state.cartIsVisible
    }
  }
})

export const cartUiActions = cartUiActions.actions
export default cartUiSlice
