import React from "react"
import { ListGroup } from "reactstrap"
import CartItem from "./CartItem"
import { Link } from "react-router-dom"
import "../../styles/shopping-cart.css"
import { useDispatch, useSelector } from "react-redux"
import { cartUiActions } from "../../store/shopping-cart/cartUiSlice"

const Cart = () => {
  const dispatch = useDispatch()
  const cartProdcuts = useSelector((state) => state.cart.cartItems)
  const totalAmount = useSelector((state) => state.cart.totalAmount);
  const cartProducts  = useSelector(state=> state.cart.cartItems)
  const toggleCart = () => {
    dispatch(cartUiActions.toggleCart)
  }
  return (
    <div className='cart__container'>
      <ListGroup className='cart'>
        <div className='cart_close'>
          <span onClick={toggleCart}>
            <i class='ri-close-fill'></i>
          </span>
        </div>

        <div className='cart__item-list'>
          {cartProducts.length === 0 ? (
            <h6>No item added to the cart</h6>
          ) : (
            cartProducts.map((item, index) => (
              <CartItem item={item} key={index} />
            ))
          )}
        </div>
        <div className='cart__bottom d-flex align-items-center'>
          <h6 className='d-flex align-items-center justify-content-space-between'>
            Subtotal amount: <span>$123</span>
          </h6>
          <button className='checkout-list d-flex align-item-center'>
            <Link to='/checkout'>Checkout</Link>
          </button>
        </div>
      </ListGroup>
    </div>
  )
}

export default Cart
